// src/services/tryOnService.ts

export interface TryOnRequest {
  personImage: string;    // Base64 或 URL
  garmentImage: string;   // 衣服图片
  category: 'upper' | 'lower' | 'dress';
}

export interface TryOnResponse {
  resultImage: string;    // 生成的结果图
  processingTime: number;
  taskId: string;
}

// 推荐的 AI 服务提供商:
// 1. Replicate - fashn/tryon 模型
// 2. Kolors Virtual Try-On
// 3. IDM-VTON

export async function processVirtualTryOn(
  request: TryOnRequest
): Promise<TryOnResponse> {
  // 调用 Supabase Edge Function 作为代理
  const response = await supabase.functions.invoke('virtual-try-on', {
    body: request
  });

  return response.data;
}
```

